
public class AllTables {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int m = 1; m<=10;m++)
		{
			for(int n=1;n<=10;n++)
			{
				if(n<9)
				System.out.println(m + " * "+ n +" = "+ n*m);
			}
		//	System.out.print(" ");
		}

	}

}
